import{_ as e,c as o,o as t,V as a}from"./chunks/framework.Dy019jra.js";const u=JSON.parse('{"title":"友人志：马哥——铃未鸣记","description":"","frontmatter":{},"headers":[],"relativePath":"friends/01-long-bell-not-rang.md","filePath":"friends/01-long-bell-not-rang.md"}'),s={name:"friends/01-long-bell-not-rang.md"},n=a('<h1 id="友人志-马哥——铃未鸣记" tabindex="-1">友人志：马哥——铃未鸣记 <a class="header-anchor" href="#友人志-马哥——铃未鸣记" aria-label="Permalink to &quot;友人志：马哥——铃未鸣记&quot;">​</a></h1><blockquote><p>泷 2022.3.二十五六号吧</p></blockquote><p>马总行事而脱乎，使朝、暮二铃不鸣，遂余作文以记之<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup>。</p><p>处枪决<sup class="footnote-ref"><a href="#fn2" id="fnref2">[2]</a></sup>前七十九夜，吾与马二人同归宿舍，马怅然，曰：“龙，吾行一事而脱，呜呼。”曰：“嘻，何出此言？”马曰：“呜呼，吾晚三游于级部，用校园广播系统，不慎关之，吾慌遂重启而未成，故夜、翌旦二铃不鸣。”曰：“甚善，吾休于眠至卯、辰之时，此皆马总之功也。”马曰：“吾惧甚遂删其日志。呜呼，此事脱矣。”曰：“无妨，如使朝明灯，暮熄灯之时而未鸣，余定将作文以记之，以谢马总之恩惠。”</p><p>至暮熄灯时，铃未鸣遂饮 AD 钙，曰“马总牛通<sup class="footnote-ref"><a href="#fn3" id="fnref3">[3]</a></sup>。”翌日，宿管惊曰：“时以至此，何不起乎？”遂奔大操场。</p><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>选自《回忆录》作文者：泷也，马之友也。提字者，菡也，马友之也。泷誊写此文以赠马总。 <a href="#fnref1" class="footnote-backref">↩︎</a></p></li><li id="fn2" class="footnote-item"><p>[枪决]: 泷与菡之师，殿信，不善词藻，一日愤而曰：“高考未足六百者，皆引之而枪决。”枪决，语出于此。 <a href="#fnref2" class="footnote-backref">↩︎</a></p></li><li id="fn3" class="footnote-item"><p>[牛通]:“通”者，“逼”也。小破站给贰贰百大之最佳新人奖，其一才子名曰：“苏星河牛通”，软件测评人也。文案善，甚走心，常引人深思，实属稀缺之人材也。牛通，语出于此。 <a href="#fnref3" class="footnote-backref">↩︎</a></p></li></ol></section>',7),f=[n];function r(l,c,i,p,_,d){return t(),o("div",null,f)}const m=e(s,[["render",r]]);export{u as __pageData,m as default};
